UPDATE `locales_quest` SET `title_loc6`='Sombras acechadoras',`details_loc6`='El libro lleva la marca de El Athenaeum.',`objectives_loc6`='Devuelve el libro a sus legítimos dueños.' WHERE `entry`=7502;
