UPDATE `locales_quest` SET `title_loc6`='Asistencia material',`details_loc6`='$N, hay un asunto urgente que creo que te interesará material y espiritualmente.$B$BEscondida en Scholomance, en Lordaeron, hay una cámara que se llama El Gran Osario. Quiero ultimar un instrumento que ahuyente las sombras de esa horrible sala. Para eso, necesito dos diamantes: uno de Azeroth y uno negro prístino. Sé que lo que pido no es fácil, pero no puedo hacerlo por mí mismo.$B$B¿Puedes traérmelos tú?',`objectives_loc6`='Si te interesa ayudar a Sagorne Montacresta, llévale 1 diamante de Azeroth y 1 diamante negro prístino al Valle de la Sabiduría, en Orgrimmar.' WHERE `entry`=7667;
