UPDATE `locales_quest` SET `title_loc6`='Maestro de arena',`details_loc6`='¡Ser un maestro de la arena tiene que ser algo más que esto! ¡Quizás lo sepa el pirata que lleva el evento en Gurubashi!',`objectives_loc6`='Habla con Corto John Mitril en la Arena de la Vega de Tuercespina.' WHERE `entry`=7810;
