UPDATE `locales_quest` SET `title_loc6`='Embozo de sabiduría infinita',`details_loc6`='Vaya, ¡cómo me alegra oír que nos ayudarás en el ataque a Ahn\'Qiraj, $N! Puedo prepararte una protección que te ayudará en el combate contra el enemigo, $C.$B$BNecesitaré un manto qiraji, al que le daré tus medidas, claro. También necesitaré ídolos y escarabajos para confeccionarte un pasador apropiado.$B$BTe lo prometo, no te decepcionaré.',`objectives_loc6`='Llévale 1 mantón marcial qiraji, 2 ídolos de jaspe, 5 escarabajos de oro y 5 escarabajos de terracota a Keyl Patacerce en Silithus. También debes alcanzar la reputación de venerado ante el Círculo Cenarion para completar esta misión.' WHERE `entry`=8689;
