UPDATE `locales_quest` SET `title_loc6`='Metzen el Reno',`details_loc6`='¡Qué desastre! ¡Han secuestrado a Metzen el Reno!$B$BEs uno de los ocho renos de Padre Invierno y propiedad de Pastos de Bosquehumeante. Creemos que se lo ha llevado el ruin de Grinch el Abominable, que se esconde en las Laderas de Trabalomas. ¡Y precisamente ahora, con la fiesta, andamos ajustados de dinero! Por favor, ¡tráenos a Metzen, derrota a sus captores y tráenoslo de vuelta!',`objectives_loc6`='Rescata a Metzen el reno de sus captores cerca de la Caverna Estrecha, en las Laderas de Trabalomas.' WHERE `entry`=8746;
