UPDATE `locales_quest` SET `title_loc6`='Objetivo: los defensores Colmen\'Ashi',`details_loc6`='Según progresa la guerra, la presencia de silítidos en la zona supone una amenaza constante para nuestras operaciones. Te han seleccionado para participar en un asalto a Colmen\'Ashi donde tu objetivo principal consistirá en los defensores Colmen\'Ashi.$B$BInforma a la comandante Mar\'alith cuando hayas terminado tu misión.',`objectives_loc6`='Mata 30 defensores Colmen\'Ashi y preséntate ante el comandante Mar\'alith en el Fuerte Cenarion, en Silithus. También debes llevar el Informe de tareas de combate I para poder completar la misión.' WHERE `entry`=8770;
UPDATE `locales_quest` SET `title_loc6`='Objetivo: los acecharenas Colmen\'Ashi',`details_loc6`='Según progresa la guerra, la presencia de silítidos en la zona supone una amenaza constante para nuestras operaciones. Te han seleccionado para participar en un asalto a Colmen\'Ashi donde tu objetivo principal consistirá en los acecharenas Colmen\'Ashi.$B$BInforma al comandante Mar\'alith cuando hayas terminado u misión.',`objectives_loc6`='Mata 30 acecharenas Colmen\'Ashi y preséntate ante el comandante Mar\'alith en el Fuerte Cenarion, en Silithus. También debes llevar el Informe de tareas de combate II para poder completar la misión.' WHERE `entry`=8771;
UPDATE `locales_quest` SET `title_loc6`='Objetivo: los oteadores de Colmen\'Zora',`details_loc6`='Se han pedido refuerzos para unirse al asalto a Colmen\'Zora. Te han seleccionado para atacar a los oteadores de Colmen\'Zora. Informa a la comandante Mar\'alith cuando hayas completado tu misión.',`objectives_loc6`='Mata 30 oteadores de Colmen\'Zora y preséntate ante el comandante Mar\'alith en el Fuerte Cenarion, en Silithus. También debes llevar el Informe de tareas de combate VI para poder completar la misión.|n' WHERE `entry`=8772;
