UPDATE `locales_quest` SET `title_loc6`='Vendas para el campo de batalla',`details_loc6`='Debido a las víctimas que hemos tenido recientemente, nuestro suministro de vendajes en el campo de batalla empieza a escasear peligrosamente. Consigue un lote de suministros de primeros auxilios y llévaselo a Clamavientos Cuerno Digno en el Fuerte Cenarion.',`objectives_loc6`='Lleva 30 vendas de paño rúnico gruesas, 30 vendas de seda gruesas y 30 vendas de tejido mágico gruesas a Clamavientos Cuerno Digno en el Fuerte Cenarion, en Silithus. También debes llevar el Informe de tareas logísticas X para poder completar la misión.' WHERE `entry`=8810;
