UPDATE `locales_quest` SET `title_loc6`='El Festival Lunar',`details_loc6`='¡Ha llegado ese momento del año, $N! El Círculo Cenarion invita a todos los ciudadanos de Azeroth a participar en la celebración del Festival Lunar. Encontrarás a la presagista del Festival Lunar en esta misma ciudad. Busca el lugar marcado por un círculo sagrado de Luz de Luna. Ella podrá decirte más cosas sobre el Festival.',`objectives_loc6`='Habla con la presagista del Festival Lunar en el Valle de la Sabiduría, en Orgrimmar. También puedes hablar con la presagista del Festival Lunar en otras capitales.' WHERE `entry`=8873;
