UPDATE `locales_quest` SET `title_loc6`='Una propuesta seria',`details_loc6`='¡Saludos, $C! Se diría que has hecho y visto mucho en este mundo. Pero apuesto a que nunca has visto una armadura como esta.$B$BTe la cambiaré por un par de brazales normales si me haces un favor.$B$BNecesito bastante veneno de araña y escorpión de Silithus. Tráeme el veneno y algunas monedas de oro y haremos el intercambio.',`objectives_loc6`='Lleva 15 muestras de veneno de Silithus, 20 piezas de oro y 1 par de brazales Corazón Salvaje a Mokvar a Orgrimmar.' WHERE `entry`=8913;
