UPDATE `locales_quest` SET `title_loc6`='Carta del frente',`details_loc6`='Has encontrado esta carta en uno de los muchos esbirros de la Plaga que has liquidado. Por el aspecto y el olor, el no-muerto la llevó consigo durante bastante tiempo. Puede que a alguien de la Capilla de la Esperanza de la Luz le interese examinarla más atentamente...',`objectives_loc6`='Lleva la carta rota al Guardián de los Pergaminos de la Capilla de la Esperanza de la Luz, en las Tierras de la Peste del Este.' WHERE `entry`=9295;
