UPDATE `locales_npc_text` SET `Text0_0_loc5`='菲拉斯的土地經歷了歲月的洗禮，$R。大地、樹木和生物，它們改變著菲拉斯的現狀和未來。$B$B跟這兒所發生的一切相比，雙塔山可以算是亙古不變的了...' WHERE `entry`=2053;
