UPDATE `locales_npc_text` SET `Text0_0_loc5`='現在還不是讓你長眠的時候。我會幫你起死回生...但你也要為此付出一些代價。' WHERE `entry`=6491;
UPDATE `locales_npc_text` SET `Text0_0_loc5`='我們的祖先教誨說，集體的力量勝過個人的力量。你想要加入公會為了雷霆崖的榮譽而戰嗎?' WHERE `entry`=6513;
