UPDATE `locales_npc_text` SET `Text0_0_loc5`='嗨，勇士。你的事蹟將會被世代傳誦。或許有一天，你的雕像會矗立在暴風城的英雄之間。' WHERE `entry`=9066;
UPDATE `locales_npc_text` SET `Text0_0_loc5`='離我遠一點，$R!當病痛再次對我纏身就不要再和我接近了。$B$B在它再發生之前不要再來找我。' WHERE `entry`=9067;
UPDATE `locales_npc_text` SET `Text0_0_loc5`='檢查屍體你推論這一定是血騎士晨星，死亡之域失敗突擊的領導者。' WHERE `entry`=9070;
UPDATE `locales_npc_text` SET `Text0_0_loc5`='我可以幫你建立一個公會。' WHERE `entry`=9071;
UPDATE `locales_npc_text` SET `Text0_0_loc5`='尋找屍體的衣服和裝備讓你發現了需要的徽記，未受戰役和惡劣環境的損壞。' WHERE `entry`=9072;
UPDATE `locales_npc_text` SET `Text0_0_loc5`='伊露恩的仁慈降臨到你身上。我能幫你什麼?' WHERE `entry`=9073;
UPDATE `locales_npc_text` SET `Text0_0_loc5`='你好，$C。你決定來拜訪我們是對的。' WHERE `entry`=9074;
