UPDATE `trans_words` SET `content_loc3`='Exellent $N !$B,you got everything just as i asked and now i can make a Bonescythe Breastplate.' WHERE `word_id` = 144946; -- locales_quest - offerrewardtext : entry=9077
UPDATE `trans_words` SET `content_loc3`='Exellent $N !$B,you got everything just as i asked and now i can make a Bonescythe Helmet.' WHERE `word_id` = 144951; -- locales_quest - offerrewardtext : entry=9079
UPDATE `trans_words` SET `content_loc3`='Exellent $N !,$Byou got everything just as i asked and now i can make a Frostfire Bindings.' WHERE `word_id` = 145004; -- locales_quest - offerrewardtext : entry=9102
