UPDATE `trans_words` SET `content_loc7`='<HTML><BODY><BR/><BR/><P align="left">Here lies Durotan - first Chieftain of the Frostwolf Clan, and father of our honored Warchief, Thrall. He was the bravest of our kind - betrayed by those who would see our people enslaved. Durotan gave his life that our freedom might be gained. We honor him - and the legacy he passed on to us through his son.</P><BR/><P align="left">Drek\'Thar, Far Seer of the Frostwolves </P></BODY></HTML>' WHERE `word_id` = 125644; -- locales_page_text - text : entry=2731
UPDATE `trans_words` SET `content_loc7`='<HTML><BODY><BR/><BR/><P>In memory of my dear mentor, Horatio M. Montgomery, M.D. Healer, Teacher, Friend.</P><BR/><H1 align="center">50 BTFT - 25 ATFT</H1><BR/><P>"The world is full of the sick and weary. It is our job, as healers, NAY, as men and women of medicine, to cleanse them ALL of the \'itis.\'" </P><BR/><P>- H.M.M., M.D., PhD, JD, Grandmaster Farmer, Dancer Extraordinaire, Friend to the Animals</P><BR/></BODY></HTML>' WHERE `word_id` = 125011; -- locales_page_text - text : entry=1031
