UPDATE `trans_words` SET `content_loc7`='<HTML><BODY><BR/><P>Loosely translated version...</P><BR/><P>By the light, by the light of the sun,</P><P>Children of the blood,</P><P>Our enemies are breaking through.</P><P>Children of the blood,</P><P>By the light,</P><P>Failing children of the blood,</P><P>They are breaking through.</P><P>O children of the blood,</P><P>By the light of the sun.</P><P>Failing children of the blood,</P><P>They are breaking through.</P><P>O children of the blood,</P><P>By the light of the sun,</P><P>The sun.</P></BODY></HTML>' WHERE `word_id` = 125821; -- locales_page_text - text : entry=2991
