UPDATE `trans_words` SET `content_loc7`='<HTML><BODY><H1 align="center">A TREATISE ON MILITARY RANKS</H1><BR/><BR/><IMG src="Interface\PvPRankBadges\PvPRankAlliance" align="left"/><BR/><P align="right">What follows are</P><P align="right">the military ranks</P><P align="right">of the Alliance,</P><P align="right">proceeded by such</P><P align="right">information as is</P><P align="right">known for the races</P><P align="right">of the Horde. Each</P><P align="right">is presented in two</P><P align="right">sections, the officers and then the enlisted, with the ranks of each listed in descending order from highest to lowest. Long live the Alliance!</P></BODY></HTML>' WHERE `word_id` = 125619; -- locales_page_text - text : entry=2654
