UPDATE `trans_words` SET `content_loc7`='<HTML><BODY><H1 align="center">Chapter 1: THE NORTHERN PYLON</H1><BR/><BR/><IMG src="Interface\Pictures\11482_crystals_mini_north" align="left"/><P align="right">The Northern<BR/>Pylon accepts<BR/>yellow, red,<BR/>and green<BR/>crystals.</P><BR/><BR/><BR/><BR/><P align="left">The Pylon focuses on the energy that can be created using yellow crystals as the main source of power.</P></BODY></HTML>' WHERE `word_id` = 125073; -- locales_page_text - text : entry=1511
