UPDATE `trans_words` SET `content_loc7`='<HTML><BODY><H1 align="center">CRYSTAL PYLON USER\'S MANUAL</H1><BR/><P align="center"> "A guide to collecting and using the power crystals of Un\'Goro Crater" </P><BR/><BR/><P align="left">Chapter 1: The Northern Pylon </P><BR/><P align="left">Chapter 2: The Eastern Pylon </P><BR/><P align="left">Chapter 3: The Western Pylon </P><BR/></BODY></HTML>' WHERE `word_id` = 125071; -- locales_page_text - text : entry=1491
UPDATE `trans_words` SET `content_loc7`='<HTML><BODY><H1 align="center">Eastern Crystal Combinations</H1><BR/><P align="left">There are two possible effects: one provides bonus to spirit, and one that creates a damaging shield.</P><BR/><P align="left">CRYSTAL FORCE: Requires blue and green crystals.<BR/>This will provide you with an increase in spirit.</P><BR/><P align="left">CRYSTAL SPIRE: Requires blue and yellow crystals.<BR/>This will create a shield that does damage to attackers.</P></BODY></HTML>' WHERE `word_id` = 125077; -- locales_page_text - text : entry=1515
UPDATE `trans_words` SET `content_loc7`='<HTML><BODY><H1 align="center">ENLISTED RANKS OF THE ALLIANCE</H1><BR/><IMG src="Interface\PvPRankBadges\PvPRank05" align="left"/><BR/><P align="right">Sergeant Major</P><BR/><BR/><IMG src="Interface\PvPRankBadges\PvPRank04" align="left"/><BR/><P align="right">Master Sergeant</P><BR/><BR/><IMG src="Interface\PvPRankBadges\PvPRank03" align="left"/><BR/><P align="right">Sergeant</P><BR/><BR/><IMG src="Interface\PvPRankBadges\PvPRank02" align="left"/><BR/><P align="right">Corporal</P><BR/><BR/><IMG src="Interface\PvPRankBadges\PvPRank01" align="left"/><BR/><P align="right">Private</P><BR/><BR/></BODY></HTML>' WHERE `word_id` = 268936; -- locales_page_text - text : entry=2657
