UPDATE `trans_words` SET `content_loc7`='<HTML><BODY><H1 align="center">HORDE MILITARY RANKS</H1><BR/><P align="center">OFFICERS</P><BR/><P align="center">High Warlord</P><P align="center">Warlord</P><P align="center">General</P><P align="center">Lieutenant General</P><P align="center">Champion</P><P align="center">Centurion</P><P align="center">Legionnaire</P><P align="center">Blood Guard</P><P align="center">Stone Guard</P><BR/><P align="center">ENLISTED</P><BR/><P align="center">First Sergeant</P><P align="center">Senior Sergeant</P><P align="center">Sergeant</P><P align="center">Grunt</P><P align="center">Scout</P></BODY></HTML>' WHERE `word_id` = 125636; -- locales_page_text - text : entry=2677
UPDATE `trans_words` SET `content_loc7`='<HTML><BODY><H1 align="center">MILITARY RANKS OF THE HORDE &amp; ALLIANCE</H1><BR/><BR/><IMG src="Interface\PvPRankBadges\PvPRankHorde" align="left"/><BR/><P align="right">Throm\'ka!  In this</P><P align="right">book you will find</P><P align="right">many listings of</P><P align="right">the military ranks</P><P align="right">of the Horde and</P><P align="right">the puny Alliance.</P><P align="right">First will come the</P><P align="right">Horde, then the Alliance,</P><P align="right">each with listings of officers and enlisted ranks. As is fitting, the strongest are listed at the top, with the weaker listed below them.</P></BODY></HTML>' WHERE `word_id` = 268943; -- locales_page_text - text : entry=2661
UPDATE `trans_words` SET `content_loc7`='<HTML><BODY><H1 align="center">Northern Crystal Combinations</H1><BR/><P align="left">There are two possible effects: one that will produce damage, and one that will negate damage. </P><BR/><P align="left">CRYSTAL CHARGE: Requires red and yellow crystals. <BR/>This will produce a large explosion.</P><BR/><P align="left">CRYSTAL RESTORE: Requires yellow and green crystals. <BR/>This will provide you with a healing aura.</P></BODY></HTML>' WHERE `word_id` = 125074; -- locales_page_text - text : entry=1512
