UPDATE `trans_words` SET `content_loc7`='<HTML><BODY><H1 align="center">OFFICER RANKS OF THE HORDE</H1><BR/><P align="center">Part 2</P><IMG src="Interface\PvPRankBadges\PvPRank09" align="left"/><BR/><P align="right">Centurion</P><BR/><BR/><IMG src="Interface\PvPRankBadges\PvPRank08" align="left"/><BR/><P align="right">Legionnaire</P><BR/><BR/><IMG src="Interface\PvPRankBadges\PvPRank07" align="left"/><BR/><P align="right">Blood Guard</P><BR/><BR/><IMG src="Interface\PvPRankBadges\PvPRank06" align="left"/><BR/><P align="right">Stone Guard</P><BR/><BR/></BODY></HTML>' WHERE `word_id` = 125627; -- locales_page_text - text : entry=2663
UPDATE `trans_words` SET `content_loc7`='<HTML><BODY><H1 align="center">Western Crystal Combinations</H1><BR/><P align="left">There are two possible effects: one that increases your protection from damage, and one that weakens an enemy\'s defenses.</P><BR/><P align="left">CRYSTAL WARD: Requires red and green crystals.<BR/>This will provide you with an increase to armor.</P><BR/><P align="left">CRYSTAL YIELD: Requires blue and red crystals.<BR/>This can lower the armor of a specific target.</P></BODY></HTML>' WHERE `word_id` = 125081; -- locales_page_text - text : entry=1532
