UPDATE `trans_words` SET `content_loc7`='Check Second Cage' WHERE `word_id` = 137654; -- locales_quest - ObjectiveText2 : entry=2988
UPDATE `trans_words` SET `content_loc7`='Chemist Cuely has been very accommodating... disturbingly so. But if he says this will help me, then I have no other choice. I don\'t have time to find any other solution. I\'ve already spoken to priests and shamans, none of which could help me.$B$BLet us hope that the Earthmother watches over my spirit.' WHERE `word_id` = 275847; -- locales_quest - offerrewardtext : entry=3569
UPDATE `trans_words` SET `content_loc7`='Chest opens to reveal Rackmore\'s treasure!' WHERE `word_id` = 141108; -- locales_quest - offerrewardtext : entry=6161
UPDATE `trans_words` SET `content_loc7`='Chief Hawkwind sent you? Embarking on the Rites of the Earthmother is no small task...' WHERE `word_id` = 133378; -- locales_quest - offerrewardtext : entry=755
UPDATE `trans_words` SET `content_loc7`='Choose your reward and let the celebration of your glorious victory begin.' WHERE `word_id` = 142113; -- locales_quest - offerrewardtext : entry=7491
UPDATE `trans_words` SET `content_loc7`='Choose your reward so that I may mount the head of the beast for all to see.' WHERE `word_id` = 142463; -- locales_quest - offerrewardtext : entry=7784
