UPDATE `trans_words` SET `content_loc5`='Bring 1 Large Brilliant Shard, 1 Large Radiant Shard and 1 Huge Emerald to Geologist Larksbane at Cenarion Hold in Silithus. You must also bring Logistics Task Briefing V in order to complete this quest.' WHERE `word_id` = 144312; -- locales_quest - objectives : entry=8779
