UPDATE `trans_words` SET `content_loc5`='Countless times I urged the Warchief not to trust the humans, but personal pride is not what is at stake here.$B$BYou have served the Horde honorably, young $C.$B$BNow excuse me, I must counsel Thrall on these matters at once...' WHERE `word_id` = 133641; -- locales_quest - offerrewardtext : entry=831
UPDATE `trans_words` SET `content_loc5`='Cracked it open, did you? Nice work, $N.$B$BIf you die now, we can rest easy knowing it was due to your own stupidity and not inexperience.' WHERE `word_id` = 136745; -- locales_quest - offerrewardtext : entry=2282
