UPDATE `trans_words` SET `content_loc5`='Dithers said you\'d deliver the goods, and deliver you have! A deal is a deal; let me pack the fragments into the mold for you.$B$BDid Dithers imbue them already? Excellent... otherwise, it would have been a long trip back to the Bulwark for you.' WHERE `word_id` = 140398; -- locales_quest - offerrewardtext : entry=5514
