UPDATE `trans_words` SET `content_loc5`='Kill 10 Green Sludges and 10 Jade Oozes, and then report back to Fraggar Thundermantle in Aerie Peak.' WHERE `word_id` = 137372; -- locales_quest - objectives : entry=2877
