UPDATE `trans_words` SET `content_loc5`='Kill 10 Kurzen Commandoes and 6 Kurzen Headshrinkers.$B$BReport to Lieutenant Doren at the Rebel Camp.' WHERE `word_id` = 132603; -- locales_quest - objectives : entry=574
