UPDATE `trans_words` SET `content_loc5`='Kill 10 Marsh Murlocs, 10 Marsh Inkspewer, and 10 Marsh Flesheater, then return to Katar in the Swamp of Sorrows.' WHERE `word_id` = 135561; -- locales_quest - objectives : entry=1426
