UPDATE `trans_words` SET `content_loc5`='Kill 10 Skeletal Flayers and 10 Slavering Ghouls in Sorrow Hill.$B$BReturn to Commander Ashlam Valorfist at Chillwind Camp, Western Plaguelands.' WHERE `word_id` = 139833; -- locales_quest - objectives : entry=5092
UPDATE `trans_words` SET `content_loc5`='Kill 10 Syndicate Highwaymen and 6 Syndicate Mercenaries.$B$BReturn to Captain Nials at Refuge Pointe.' WHERE `word_id` = 133054; -- locales_quest - objectives : entry=681
UPDATE `trans_words` SET `content_loc5`='Kill 10 Syndicate Rogues and 10 Syndicate Watchmen. Return to High Executor Darthalia in Tarren Mill for your reward.' WHERE `word_id` = 132488; -- locales_quest - objectives : entry=549
UPDATE `trans_words` SET `content_loc5`='Kill 10 Torn Fin Tidehunters and 10 Torn Fin Oracles for Lieutenant Farren Orinelle in Southshore.' WHERE `word_id` = 132433; -- locales_quest - objectives : entry=536
