UPDATE `trans_words` SET `content_loc5`='Speak to Canaga Earthcaller for another Earth Sapta.' WHERE `word_id` = 135694; -- locales_quest - objectives : entry=1463
UPDATE `trans_words` SET `content_loc5`='Speak to Chief Engineer Bilgewhizzle in Gadgetzan about getting a power source for the testing kit.' WHERE `word_id` = 131168; -- locales_quest - objectives : entry=243
