UPDATE `trans_words` SET `content_loc5`='Speak to Dark Cleric Beryl in Tirisfal Glades.' WHERE `word_id` = 140594; -- locales_quest - objectives : entry=5651
UPDATE `trans_words` SET `content_loc5`='Speak to Dashel Stonefist in Stormwind.' WHERE `word_id` = 135224; -- locales_quest - objectives : entry=1246
UPDATE `trans_words` SET `content_loc5`='Speak to Dirania Silvershine in Shadowglen.' WHERE `word_id` = 138231; -- locales_quest - objectives : entry=3519
UPDATE `trans_words` SET `content_loc5`='Speak to Dispatch Commander Ruag in Stonard.' WHERE `word_id` = 137000; -- locales_quest - objectives : entry=2621
UPDATE `trans_words` SET `content_loc5`='Speak to Dorn Plainstalker in Thousand Needles.' WHERE `word_id` = 135508; -- locales_quest - objectives : entry=1394
UPDATE `trans_words` SET `content_loc5`='Speak to Duthorian Rall in Stormwind.' WHERE `word_id` = 136007; -- locales_quest - objectives : entry=1642
UPDATE `trans_words` SET `content_loc5`='Speak to Elling Trias in Stormwind.' WHERE `word_id` = 131625; -- locales_quest - objectives : entry=1247
UPDATE `trans_words` SET `content_loc5`='Speak to Eridan again in a moment.' WHERE `word_id` = 139112; -- locales_quest - objectives : entry=4442
