UPDATE `trans_words` SET `content_loc5`='Travel to island of Caer Darrow, in the south-central region of the Plaguelands, and look for any clues as to the whereabouts of the painting.' WHERE `word_id` = 140739; -- locales_quest - objectives : entry=5846
UPDATE `trans_words` SET `content_loc5`='Travel to Kalimdor, $R. Our flight makes its home in the frigid landscape of Winterspring. Once you reach Winterspring, find the caves of Mazthoril.$B$BUpon the icy floors of Mazthoril you will find cobalt runes. Hold one of my scales and stand upon a rune and you shall be transported to Haleh, our matron protectorate.$B$BGive her the scale so that she may scry what it is I have seen and felt, here in this doomed place.' WHERE `word_id` = 139975; -- locales_quest - details : entry=5160
UPDATE `trans_words` SET `content_loc5`='Travel to Kalimdor, to the coastal city of Theramore. From there you must search for an island, south of the main town. Nat Pagle awaits your arrival!' WHERE `word_id` = 141529; -- locales_quest - objectives : entry=6609
UPDATE `trans_words` SET `content_loc5`='Travel to Keeper Albagorm on Stonetalon Peak.' WHERE `word_id` = 134553; -- locales_quest - objectives : entry=1056
UPDATE `trans_words` SET `content_loc5`='Travel to Lakeshire and deliver Helendis Riverhorn\'s Letter to Magistrate Solomon.' WHERE `word_id` = 138879; -- locales_quest - objectives : entry=4183
