UPDATE `trans_words` SET `content_loc5`='Travel to Stratholme and kill the Crimson Hammersmith. Recover the Crimson Hammersmith\'s Apron and return to Lilith.' WHERE `word_id` = 271336; -- locales_quest - objectives : entry=5305
UPDATE `trans_words` SET `content_loc5`='Travel to Stratholme and search the ziggurats. Find and return new Scourge Data to Betina Bigglezink.' WHERE `word_id` = 271290; -- locales_quest - objectives : entry=5213
UPDATE `trans_words` SET `content_loc5`='Travel to Stratholme and slay Ramstein the Gorger. Take his head as a souvenir for Nathanos.' WHERE `word_id` = 141116; -- locales_quest - objectives : entry=6163
