insert  into `dbdocssubtables`(`subTableId`,`languageId`,`subTableName`,`subTableContent`,`subTableTemplate`) values (12,0,'Conditions','<table border=\'1\' cellspacing=\'1\' cellpadding=\'3\' bgcolor=\'#f0f0f0\'>\r\n<tr bgcolor=\'#f0f0ff\'>\r\n<th><b>ID</b></th>\r\n<th align=\'left\'><b>Type</b></th>\r\n<th align=\'left\'><b>Description</b></th>\r\n<tr bgcolor=\'#FFFFEE\'><td align=\'center\' valign=\'middle\'>-3</td><td align=\'left\' valign=\'middle\'>CONDITION_NOT</td><td align=\'left\' valign=\'middle\'>Used to evaluate if another condition is NOT true.</td></tr>\r\n<tr bgcolor=\'#FEFEFF\'><td align=\'center\' valign=\'middle\'>-2</td><td align=\'left\' valign=\'middle\'>CONDITION_OR</td><td align=\'left\' valign=\'middle\'>Used to evaluate if condition OR condition is true.</td></tr>\r\n<tr bgcolor=\'#FFFFEE\'><td align=\'center\' valign=\'middle\'>-1</td><td align=\'left\' valign=\'middle\'>CONDITION_AND</td><td align=\'left\' valign=\'middle\'>Used to evaluate if condition AND condition is true.</td></tr>\r\n<tr bgcolor=\'#FEFEFF\'><td align=\'center\' valign=\'middle\'>0</td><td align=\'left\' valign=\'middle\'>CONDITION_NONE</td><td align=\'left\' valign=\'middle\'>This condition is not used at all.</td></tr>\r\n<tr bgcolor=\'#FFFFEE\'><td align=\'center\' valign=\'middle\'>1</td><td align=\'left\' valign=\'middle\'>CONDITION_AURA</td><td align=\'left\' valign=\'middle\'>Checks target currently has the specified aura on him/her.</td></tr>\r\n<tr bgcolor=\'#FEFEFF\'><td align=\'center\' valign=\'middle\'>2</td><td align=\'left\' valign=\'middle\'>CONDITION_ITEM</td><td align=\'left\' valign=\'middle\'>Checks if the player has the required amount (value2) of items in his/hers inventory.</td></tr>\r\n<tr bgcolor=\'#FFFFEE\'><td align=\'center\' valign=\'middle\'>3</td><td align=\'left\' valign=\'middle\'>CONDITION_ITEM_EQUIPPED</td><td align=\'left\' valign=\'middle\'>Checks if the player has the specified item equipped.</td></tr>\r\n<tr bgcolor=\'#FEFEFF\'><