insert  into `dbdocssubtables`(`subTableId`,`languageId`,`subTableName`,`subTableContent`,`subTableTemplate`) values (60,0,'Creature Linking Flags - Combat','<table border=\'1\' cellspacing=\'1\' cellpadding=\'3\' bgcolor=\'#f0f0f0\'>\r\n<tr bgcolor=\'#f0f0ff\'>\r\n<th><b>Value</b></th>\r\n<th align=\'left\'><b>Flag Name</b></th>\r\n<th align=\'left\'><b>Description</b></th>\r\n<tr bgcolor=\'#FFFFEE\'><td align=\'center\' valign=\'middle\'>1</td><td align=\'left\' valign=\'middle\'>FLAG_AGGRO_ON_AGGRO</td><td align=\'left\' valign=\'middle\'>the slave aggroes when the master aggroes</td></tr>\r\n<tr bgcolor=\'#FEFEFF\'><td align=\'center\' valign=\'middle\'>2</td><td align=\'left\' valign=\'middle\'>FLAG_TO_AGGRO_ON_AGGRO</td><td align=\'left\' valign=\'middle\'>the master aggroes when the slave aggroes</td></tr>\r\n<tr bgcolor=\'#FFFFEE\'><td align=\'center\' valign=\'middle\'>4</td><td align=\'left\' valign=\'middle\'>FLAG_RESPAWN_ON_EVADE</td><td align=\'left\' valign=\'middle\'>the slave respawns when the master evades</td></tr>\r\n<tr bgcolor=\'#FEFEFF\'><td align=\'center\' valign=\'middle\'>8</td><td align=\'left\' valign=\'middle\'>FLAG_TO_RESPAWN_ON_EVADE</td><td align=\'left\' valign=\'middle\'>the master respawns when the slave evades</td></tr>\r\n<tr bgcolor=\'#FFFFEE\'><td align=\'center\' valign=\'middle\'>16</td><td align=\'left\' valign=\'middle\'>FLAG_DESPAWN_ON_DEATH</td><td align=\'left\' valign=\'middle\'>the slave despawns when the master dies</td></tr>\r\n<tr bgcolor=\'#FEFEFF\'><td align=\'center\' valign=\'middle\'>32</td><td align=\'left\' valign=\'middle\'>FLAG_SELFKILL_ON_DEATH</td><td align=\'left\' valign=\'middle\'>the slave goes suicide when the master dies</td></tr>\r\n<tr bgcolor=\'#FFFFEE\'><td align=\'center\' valign=\'middle\'>64</td><td align=\'left\' valign=\'middle\'>FLAG_RESPAWN_ON_DEATH</td><td align=\'left\' valign=\'middle\'>the slave respawn when the master dies</td></tr>\r\n<tr bgcolor=\'#FEFEFF\'><td align=\'center\' valign=\'middle\'>128</td><td align=\'left\' valign=\'middle\'>FLAG_RESPAWN_ON_RESPAWN</td><td align=\'left\' valign=\'middle\'>the slave respawns on master respawn</td></tr>\r\n<tr bgcolor=\'#FFFFEE\'><td align=\'center\' valign=\'middle\'>256</td><td align=\'left\' valign=\'middle\'>FLAG_DESPAWN_ON_RESPAWN</td><td align=\'left\' valign=\'middle\'>the slave despawns on master respawn</td></tr>\r\n<tr bgcolor=\'#FEFEFF\'><td align=\'center\' valign=\'middle\'>4096</td><td align=\'left\' valign=\'middle\'>FLAG_DESPAWN_ON_EVADE</td><td align=\'left\' valign=\'middle\'>the slave despawn after the master evade</td></tr>\r\n</table>\r\n','Value|<Flag Name|<Description\r\n1|FLAG_AGGRO_ON_AGGRO|the slave aggroes when the master aggroes\r\n2|FLAG_TO_AGGRO_ON_AGGRO|the master aggroes when the slave aggroes\r\n4|FLAG_RESPAWN_ON_EVADE|the slave respawns when the master evades\r\n8|FLAG_TO_RESPAWN_ON_EVADE|the master respawns when the slave evades\r\n16|FLAG_DESPAWN_ON_DEATH|the slave despawns when the master dies\r\n32|FLAG_SELFKILL_ON_DEATH|the slave goes suicide when the master dies\r\n64|FLAG_RESPAWN_ON_DEATH|the slave respawn when the master dies\r\n128|FLAG_RESPAWN_ON_RESPAWN|the slave respawns on master respawn\r\n256|FLAG_DESPAWN_ON_RESPAWN|the slave despawns on master respawn\r\n4096|FLAG_DESPAWN_ON_EVADE|the slave despawn after the master evade');
insert  into `dbdocstable`(`tableId`,`languageId`,`tableName`,`tableNotes`) values (1,0,'areatrigger_involvedrelation','The areatrigger_involvedrelation table holds connections between triggers and quests.\r\n\r\nIf there is a record in the table for a quest, the quest will not be completed until the player activates the areatriger. The quest is not necessarily finished after that, but that one condition of the quest is satisfied. If the only condition of the quest is to explore an area, then the quest will be complete.');
insert  into `dbdocstable`(`tableId`,`languageId`,`tableName`,`tableNotes`) values (2,0,'areatrigger_tavern','Enable a trigger when player enters a city or tavern. This causes the player to enter a resting state.');
insert  into `dbdocstable`(`tableId`,`languageId`,`tableName`,`tableNotes`) values (3,0,'areatrigger_teleport','Contains all the teleport triggers definition. This table is used to complete .dbc file information.');
insert  into `dbdocstable`(`tableId`,`languageId`,`tableName`,`tableNotes`) values (4,0,'battleground_events','This table contains the description of battleground events.');
insert  into `dbdocstable`(`tableId`,`languageId`,`tableName`,`tableNotes`) values (5,0,'battleground_template','Contains information about the different battlegrounds, like how many players are needed to start, how many can be inside the same one, and the locations where each side starts.');
insert  into `dbdocstable`(`tableId`,`languageId`,`tableName`,`tableNotes`) values (6,0,'battlemaster_entry','Holds information on which NPC can start what battleground or arena.');
insert  into `dbdocstable`(`tableId`,`languageId`,`tableName`,`tableNotes`) values (7,0,'command','Holds help and security information for the mangos commands.\r\n\r\nThis is also the full list of suppported mangos commands.');
insert  into `dbdocstable`(`tableId`,`languageId`,`tableName`,`tableNotes`) values (8,0,'conditions','With this table and the new conditions it is possible to create tree like and very complicated combined conditions (like HasAura && (HasItem || HasQuest))\r\n\r\nUsed in:\r\n\r\nDBScript\r\ngossip_menu\r\ngossip_menu_option\r\nnpc_spellclick_spells\r\nspell_area');
insert  into `dbdocstable`(`tableId`,`languageId`,`tableName`,`tableNotes`) values (9,0,'creature','Contains individual creature spawn data. Spawn of a creature is an instance of the creature object in the world.');
insert  into `dbdocstable`(`tableId`,`languageId`,`tableName`,`tableNotes`) values (10,0,'creature_addon','The creature_addon and creature_template_addon tables define different things that are applied on creatures when they are loaded. These \"different things\" can be for example to have the creature be mounted, to have it emote something, to have it display an aura effect, etc. Through the use of the fields in this table, many things can be changed about the outward visual appearance of the creature. The creature_template_addon table affects all creatures with that creature template ID while the creature_addon table affects individually spawned creatures (so that two creatures using the same template can look different).<br /><br />NOTES: <br />A creature_addon record will override a creature_template_addon record should they overlap on the same creature.<br /><br />The data for this table is largely incomplete and is mostly just a regurgitation of what the client receives from the server. This article is a WIP as to what all the possible values are.');
insert  into `dbdocstable`(`tableId`,`languageId`,`tableName`,`tableNotes`) values (11,0,'creature_ai_scripts','This table specifies the actions that a creature script will do. \r\nA basic eventAI script works with and requires only two pieces of information: What to do and When to do it.');
insert  into `dbdocstable`(`tableId`,`languageId`,`tableName`,`tableNotes`) values (12,0,'creature_ai_summons','This table is used to provide NPC support for an event using action 32 = ACTION_T_SUMMON as one of its Actions');
insert  into `dbdocstable`(`tableId`,`languageId`,`tableName`,`tableNotes`) values (13,0,'creature_ai_texts','This table holds all the texts used within the eventai (ACID) scripts. This table handles the actual text, display type (say/yell/emote) and corresponding sounds or emote (if any).');
insert  into `dbdocstable`(`tableId`,`languageId`,`tableName`,`tableNotes`) values (14,0,'creature_battleground','This table contains the description of creatures spawned on battlegrounds.');
insert  into `dbdocstable`(`tableId`,`languageId`,`tableName`,`tableNotes`) values (15,0,'creature_equip_template','This table contains all equipment mobs can wear.');
insert  into `dbdocstable`(`tableId`,`languageId`,`tableName`,`tableNotes`) values (16,0,'creature_equip_template_raw','The creature_equip_template_raw table holds information on items that creatures should wear.<br /><br /><b>Note:</b> This table is deprecated. Do not use it, as it will be removed in a future update and is just here to ease transition to the new creature_equip_template table.');
insert  into `dbdocstable`(`tableId`,`languageId`,`tableName`,`tableNotes`) values (17,0,'creature_involvedrelation','Holds NPC quest ender relations on which NPCs finishes which quests.');
insert  into `dbdocstable`(`tableId`,`languageId`,`tableName`,`tableNotes`) values (18,0,'creature_item_template','xxxx');
insert  into `dbdocstable`(`tableId`,`languageId`,`tableName`,`tableNotes`) values (19,0,'creature_linking','This table holds details of how creatures linked to a master creature should act in combat and non-combat situations.');
insert  into `dbdocstable`(`tableId`,`languageId`,`tableName`,`tableNotes`) values (20,0,'creature_linking_template','This table holds details on how creature templates linked to a master creature template should act in combat and non-combat situations.');
insert  into `dbdocstable`(`tableId`,`languageId`,`tableName`,`tableNotes`) values (21,0,'creature_loot_template','This table format is used to generate different loot items. Loot templates define only items in the loot.\r\nSee comments about money drop in corpse, pickpocketing and luggage loot in creature_template and item_template.');
insert  into `dbdocstable`(`tableId`,`languageId`,`tableName`,`tableNotes`) values (22,0,'creature_model_info','This table contains all models of mobs, their gender and other information that are model related. This means that when a creature uses another model, this information will change as well.');
insert  into `dbdocstable`(`tableId`,`languageId`,`tableName`,`tableNotes`) values (23,0,'creature_movement','This table holds all the information on each creature\'s waypoints. In essence, a waypoint just defines a path that the creature will follow by going from point to point. More specifically, once the creature arrives at a point, it can do different things like cast a spell, do an emote, etc. Usually this table is filled through the .wp command (and its various subcommands) in the world. \r\n\r\nPlease note that for a creature to use waypoints, its MovementType must be 2.');
insert  into `dbdocstable`(`tableId`,`languageId`,`tableName`,`tableNotes`) values (24,0,'creature_movement_template','This table holds informations on paths for creature_template entries and allows the behaviour to be defined along the movement path.<br /><br />Template movement is usually applied to creature templates spawned by scripts, or for templates which are unique and have only one spawn.<br /><br /><b>Note:</b> Movement attached to a creature template will be applied to all spawns of this template, unless there is a unique movement defined for the creature entry.');
insert  into `dbdocstable`(`tableId`,`languageId`,`tableName`,`tableNotes`) values (25,0,'creature_onkill_reputation','This table controls the reputation given by creatures when killed by other players.');
