    SET @cOldStructure = '16'; 
    SET @cOldContent = '001';

    -- New Values
    SET @cNewVersion = '21';
    SET @cNewStructure = '16'; -- If The Update contains any 'ALTER DATABASE' statements, increment this and set cNewContent to 001
